import{_ as t,c as e,o as a,a as o}from"./app.cf930e1c.js";const b='{"title":"compas","description":"","frontmatter":{},"headers":[{"level":2,"title":"compas completions","slug":"compas-completions"},{"level":2,"title":"compas check-env","slug":"compas-check-env"},{"level":2,"title":"compas code-mod","slug":"compas-code-mod"},{"level":3,"title":"compas code-mod list","slug":"compas-code-mod-list"},{"level":3,"title":"compas code-mod exec","slug":"compas-code-mod-exec"},{"level":2,"title":"compas docker","slug":"compas-docker"},{"level":3,"title":"compas docker up","slug":"compas-docker-up"},{"level":3,"title":"compas docker down","slug":"compas-docker-down"},{"level":3,"title":"compas docker clean","slug":"compas-docker-clean"},{"level":2,"title":"compas init","slug":"compas-init"},{"level":2,"title":"compas lint","slug":"compas-lint"},{"level":2,"title":"compas migrate","slug":"compas-migrate"},{"level":3,"title":"compas migrate info","slug":"compas-migrate-info"},{"level":3,"title":"compas migrate rebuild","slug":"compas-migrate-rebuild"},{"level":2,"title":"compas run","slug":"compas-run"},{"level":3,"title":"compas run $script","slug":"compas-run-script"},{"level":2,"title":"compas test","slug":"compas-test"},{"level":2,"title":"compas version","slug":"compas-version"},{"level":2,"title":"compas visualise","slug":"compas-visualise"},{"level":3,"title":"compas visualise erd","slug":"compas-visualise-erd"},{"level":2,"title":"compas help","slug":"compas-help"},{"level":2,"title":"compas watch","slug":"compas-watch"}],"relativePath":"references/cli.md"}',n={},s=o(`<h1 id="compas" tabindex="-1"><code>compas</code> <a class="header-anchor" href="#compas" aria-hidden="true">#</a></h1><p>The Compas CLI</p><p>Unified backend tooling.</p><p>Examples:</p><ul><li>compas docker up</li><li>compas lint --jsdoc</li><li>compas visualise erd --generated-directory ./src/generated</li></ul><p>References:</p><ul><li>Docs: <a href="https://compasjs.com" target="_blank" rel="noopener noreferrer">https://compasjs.com</a></li><li>Source: <a href="https://github.com/compasjs/compas" target="_blank" rel="noopener noreferrer">https://github.com/compasjs/compas</a></li><li>Issues: <a href="https://github.com/compasjs/compas/issues" target="_blank" rel="noopener noreferrer">https://github.com/compasjs/compas/issues</a></li></ul><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-completions" tabindex="-1"><code>compas completions</code> <a class="header-anchor" href="#compas-completions" aria-hidden="true">#</a></h2><p>Configure shell auto-complete for this CLI.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-check-env" tabindex="-1"><code>compas check-env</code> <a class="header-anchor" href="#compas-check-env" aria-hidden="true">#</a></h2><p>Various checks helping with a better Compas experience.</p><p>This command is able to check a few things in the current project, to see if it is optimally configured.</p><ul><li>Checks if the &#39;.env.local&#39; is in the .gitignore if it exists</li><li>Checks if all Compas packages are the same version</li></ul><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-code-mod" tabindex="-1"><code>compas code-mod</code> <a class="header-anchor" href="#compas-code-mod" aria-hidden="true">#</a></h2><p>Execute code-mods to help migrating to new Compas versions.</p><p>Since Compas generates quite a bit of boilerplate, this command can help you migrate to new Compas versions that have breaking changes in generated code. By detecting usage patterns of the generated output of a previous Compas version, it can migrate (most of) your usages to whatever the new version brings.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-code-mod-list" tabindex="-1"><code>compas code-mod list</code> <a class="header-anchor" href="#compas-code-mod-list" aria-hidden="true">#</a></h3><p>List the available code-mods.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-code-mod-exec" tabindex="-1"><code>compas code-mod exec</code> <a class="header-anchor" href="#compas-code-mod-exec" aria-hidden="true">#</a></h3><p>Execute the specified code-mod.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--name</td><td>The code-mod name to execute. (string, required)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-docker" tabindex="-1"><code>compas docker</code> <a class="header-anchor" href="#compas-docker" aria-hidden="true">#</a></h2><p>Manage common docker components.</p><p>Manages a single PostgreSQL and Minio container for use in all your local projects. It can switch between multiple PostgreSQL versions (12, 13 and 14 are supported via --postgres-version), however only a single version can be &#39;up&#39; at a time.</p><p>PostgreSQL credentials:</p><blockquote><p>postgresql://postgres:postgres@127.0.0.1:5432/postgres</p></blockquote><p>Minio credentials:</p><ul><li>ACCESS_KEY: minio</li><li>SECRET_KEY: minio123</li></ul><p>Don&#39;t use this command and secrets for your production deployment.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--postgres-version</td><td>Specify the PostgreSQL version to use. Defaults to 12. (number)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-docker-up" tabindex="-1"><code>compas docker up</code> <a class="header-anchor" href="#compas-docker-up" aria-hidden="true">#</a></h3><p>Start the managed containers.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--postgres-version</td><td>Specify the PostgreSQL version to use. Defaults to 12. (number)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-docker-down" tabindex="-1"><code>compas docker down</code> <a class="header-anchor" href="#compas-docker-down" aria-hidden="true">#</a></h3><p>Stop the managed containers.</p><p>Stop any of the containers that could possibly be started by this CLI. It ignores context and stops any PostgreSQL container started by this CLI, ignoring <code>--postgres-version</code>.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--postgres-version</td><td>Specify the PostgreSQL version to use. Defaults to 12. (number)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-docker-clean" tabindex="-1"><code>compas docker clean</code> <a class="header-anchor" href="#compas-docker-clean" aria-hidden="true">#</a></h3><p>Clean up all containers and volumes, or only the PostgreSQL databases of the specified projects.</p><p>When no arguments are passed, all created docker containers and volumes are removed.</p><p>By passing &#39;--project&#39;, it can clean up PostgreSQL databases without having to restart the containers. The flag is repeatable, so multiple projects can be cleaned at the same time. If no value is passed, it defaults to &#39;process.<wbr>env.APP_NAME&#39;.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--project</td><td>Specify the project(s) to remove. If no value is passed, the current project is read from <code>environment.APP_NAME</code>. (booleanOrString[])</td></tr><tr><td>--postgres-version</td><td>Specify the PostgreSQL version to use. Defaults to 12. (number)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-init" tabindex="-1"><code>compas init</code> <a class="header-anchor" href="#compas-init" aria-hidden="true">#</a></h2><p>Init various files in the current project.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--all</td><td>Enable &#39;--gitignore&#39;, &#39;--jsconfig&#39; and &#39;--lint-config&#39;. (boolean)</td></tr><tr><td>--gitignore</td><td>Creates or overwrites the .gitignore, with defaults for IDE(s), Yarn/NPM and caches. (boolean)</td></tr><tr><td>--jsconfig</td><td>Creates or overwrites the root jsconfig.json file, to use with the Typescript Language Server. (boolean)</td></tr><tr><td>--lint-config</td><td>Creates or overwrites .eslintrc.cjs, .eslintignore and .prettierignore files, and overwrites the &#39;prettier&#39; key in the package.json. (boolean)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-lint" tabindex="-1"><code>compas lint</code> <a class="header-anchor" href="#compas-lint" aria-hidden="true">#</a></h2><p>Lint all project files.</p><p>Uses Prettier and ESLint to lint project files.</p><p>ESLint is used for all JavaScript files and Prettier runs on JavaScript, JSON, Markdown, and YAML files. The default configuration can be initialized via &#39;compas init --lint-config&#39;.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--skip-prettier</td><td>Skip running Prettier. (boolean)</td></tr><tr><td>--skip-eslint</td><td>Skip running ESLint. (boolean)</td></tr><tr><td>--eslint-cache-location</td><td>Location of ESLint cache directory. Defaults to &#39;.cache/eslint/&#39;. (string)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-migrate" tabindex="-1"><code>compas migrate</code> <a class="header-anchor" href="#compas-migrate" aria-hidden="true">#</a></h2><p>Run PostgreSQL migrations via the @compas/store migration system.</p><p>The migrations are managed via the @compas/store provided system. And are forward only.</p><p>A custom Postgres connection object can be provided by exporting a &#39;postgresConnectionSettings&#39; object from the files specified via the &#39;--connection-settings&#39; flag.</p><p>This command can keep running if for example your deploy system does not support one of tasks. You can use &#39;--keep-alive&#39; for that. It keeps a single Postgres connection alive to ensure that the process doesn&#39;t exit. The migration runner uses an advisory lock to ensure only a single migration process runs at the same time. To disable this behaviour when the command enters watch mode, &#39;--without-lock&#39; can be passed.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--connection-settings</td><td>Specify a path that contains the PostgreSQL connection object. (string)</td></tr><tr><td>--keep-alive</td><td>Keep the service running, by maintaining a single idle SQL connection. (boolean)</td></tr><tr><td>--without-lock</td><td>Drop the migration lock, before entering the keep-alive state. Only used when <code>--keep-alive</code> is passed as well (boolean)</td></tr><tr><td>--watch</td><td>Run the command, restarting it when file changes happen. See &#39;compas help watch&#39; for more information. (boolean)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-migrate-info" tabindex="-1"><code>compas migrate info</code> <a class="header-anchor" href="#compas-migrate-info" aria-hidden="true">#</a></h3><p>Print the current migration state.</p><p>Print information about the migration state and exit. The information consists of migrations that are not applied yet, and migrations that have &#39;hashChanges&#39;, basically saying that the file on disk is out of sync with the migration that was applied in the past.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--connection-settings</td><td>Specify a path that contains the PostgreSQL connection object. (string)</td></tr><tr><td>--keep-alive</td><td>Keep the service running, by maintaining a single idle SQL connection. (boolean)</td></tr><tr><td>--without-lock</td><td>Drop the migration lock, before entering the keep-alive state. Only used when <code>--keep-alive</code> is passed as well (boolean)</td></tr><tr><td>--watch</td><td>Run the command, restarting it when file changes happen. See &#39;compas help watch&#39; for more information. (boolean)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-migrate-rebuild" tabindex="-1"><code>compas migrate rebuild</code> <a class="header-anchor" href="#compas-migrate-rebuild" aria-hidden="true">#</a></h3><p>Recreate migration state based on the file system.</p><p>Rebuild migration table with current file state. This allows for reordering migrations, squashing migrations and other things that alter the migration files, but do not affect the schema in any way. Note that Compas can&#39;t enforce any consistency between the migration files and the current schema state. So use with caution.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--connection-settings</td><td>Specify a path that contains the PostgreSQL connection object. (string)</td></tr><tr><td>--keep-alive</td><td>Keep the service running, by maintaining a single idle SQL connection. (boolean)</td></tr><tr><td>--without-lock</td><td>Drop the migration lock, before entering the keep-alive state. Only used when <code>--keep-alive</code> is passed as well (boolean)</td></tr><tr><td>--watch</td><td>Run the command, restarting it when file changes happen. See &#39;compas help watch&#39; for more information. (boolean)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-run" tabindex="-1"><code>compas run</code> <a class="header-anchor" href="#compas-run" aria-hidden="true">#</a></h2><p>Run arbitrary JavaScript files, scripts defined in the package.json and scripts located in the scripts directory.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--script-args</td><td>undefined (string)</td></tr><tr><td>--node-args</td><td>undefined (string)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-run-script" tabindex="-1"><code>compas run $script</code> <a class="header-anchor" href="#compas-run-script" aria-hidden="true">#</a></h3><p>The file or script to run.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--watch</td><td>Run the command, restarting it when file changes happen. See &#39;compas help watch&#39; for more information. (boolean)</td></tr><tr><td>--script-args</td><td>undefined (string)</td></tr><tr><td>--node-args</td><td>undefined (string)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-test" tabindex="-1"><code>compas test</code> <a class="header-anchor" href="#compas-test" aria-hidden="true">#</a></h2><p>Run all tests in your project.</p><p>The test runner searches for all files ending with &#39;.test.js&#39; and runs them. Tests run in series in a single worker and subtests run serially in the order they are defined. If &#39;--serial&#39; is not passed, there will be multiple workers each executing parts of the tests.</p><p>Test files should be ordinary JavaScript files. By calling &#39;mainTestFn&#39; at the top of your file you can still use &#39;node ./path/to/file.test.js&#39; to execute the tests.</p><p>Global configuration can be applied to the test runners via a &#39;test/config.js&#39; file. A global timeout can be configured by setting &#39;export const timeout = 2500;&#39;. The value is specified in milliseconds. There is also a global &#39;setup&#39; and &#39;teardown&#39; function that can be exported from the &#39;test/config.js&#39; file. They may return a Promise.</p><p>To prevent flaky tests, &#39;--randomize-rounds&#39; can be used. This shuffles the order in which the tests are started. And prevents dependencies between test files. Making it easier to run a single test file via for examples &#39;compas run ./path/to/file.test.js&#39;.</p><p>Collecting and processing coverage information is done using C8. Use one of the supported configuration files by C8 to alter its behaviour. See <a href="https://www.npmjs.com/package/c8" target="_blank" rel="noopener noreferrer">https://www.npmjs.com/package/c8</a> for more information.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--serial</td><td>Run tests serially instead of in parallel. Alternatively set &#39;--parallel-count 1&#39; (boolean)</td></tr><tr><td>--parallel-count</td><td>The number of workers to use, when running in parallel. Defaulting to (the number of CPU cores - 1) or 4, whichever is lower. (number)</td></tr><tr><td>--randomize-rounds</td><td>Runs test the specified amount of times, shuffling the test file order between runs. (number)</td></tr><tr><td>--coverage</td><td>Collect coverage information while running the tests. (boolean)</td></tr><tr><td>--watch</td><td>Run the command, restarting it when file changes happen. See &#39;compas help watch&#39; for more information. (boolean)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-version" tabindex="-1"><code>compas version</code> <a class="header-anchor" href="#compas-version" aria-hidden="true">#</a></h2><p>Print the installed Compas version and exit</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-visualise" tabindex="-1"><code>compas visualise</code> <a class="header-anchor" href="#compas-visualise" aria-hidden="true">#</a></h2><p>Visualise various code-generated structures.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h3 id="compas-visualise-erd" tabindex="-1"><code>compas visualise erd</code> <a class="header-anchor" href="#compas-visualise-erd" aria-hidden="true">#</a></h3><p>Visualise entity structure and relations in a diagram.</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>--generated-directory</td><td>The directory containing the generated files. (string, required)</td></tr><tr><td>--format</td><td>Output file format. Supports png, webp, pdf and svg. Defaults to svg. (string)</td></tr><tr><td>--output</td><td>Path to write the output to. Defaults to a random temporary file. (string)</td></tr><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-help" tabindex="-1"><code>compas help</code> <a class="header-anchor" href="#compas-help" aria-hidden="true">#</a></h2><p>Display help for any of the available commands.</p><p>// TODO</p><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table><h2 id="compas-watch" tabindex="-1"><code>compas watch</code> <a class="header-anchor" href="#compas-watch" aria-hidden="true">#</a></h2><p>Run the command, restarting it when file changes happen.</p><p>Some commands in this CLI can be watched. They can be executed via <code>compas watch [..subCommand]</code> or by adding the &#39;--watch&#39; flag when invoking the command.</p><p>The watching happens by monitoring all the files in your project and restarting the command once files are changed. Manually restarting is also possible by sending <code>rs&lt;enter&gt;</code> to the program.</p><p>Watch behaviour can be tuned by commands. Setting &#39;modifiers.isWatchable&#39; to &#39;true&#39; is necessary for it to allow watching, and &#39;watchSettings&#39; can be specified with custom extensions to be watched or specific directories to ignore. When watch behavior is needed for custom scripts, following the steps in <a href="https://compasjs.com/features/extending-the-cli.html" target="_blank" rel="noopener noreferrer">extending the cli</a> is mandatory.</p><div class="language-js"><pre><code><span class="token keyword">export</span> <span class="token keyword">const</span> cliDefinition <span class="token operator">=</span> <span class="token punctuation">{</span>
  <span class="token literal-property property">name</span><span class="token operator">:</span> <span class="token string">&quot;my-command&quot;</span><span class="token punctuation">,</span>
  <span class="token literal-property property">shortDescription</span><span class="token operator">:</span> <span class="token string">&quot;My command&quot;</span><span class="token punctuation">,</span>
  <span class="token literal-property property">modifiers</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token literal-property property">isWatchable</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span> <span class="token comment">// This is mandatory</span>
  <span class="token punctuation">}</span><span class="token punctuation">,</span>
  <span class="token literal-property property">watchSettings</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token literal-property property">extensions</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token string">&quot;js&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;ts&quot;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token comment">// Defaults to &#39;[&quot;js&quot;, &quot;json&quot;]&#39;</span>
    <span class="token literal-property property">ignorePatterns</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token string">&quot;__fixtures__&quot;</span><span class="token punctuation">]</span><span class="token punctuation">,</span> <span class="token comment">// Defaults to &#39;[&quot;.cache&quot;, &quot;coverage&quot;, &quot;node_modules&quot;]&#39;</span>
  <span class="token punctuation">}</span><span class="token punctuation">,</span>
<span class="token punctuation">}</span><span class="token punctuation">;</span>
</code></pre></div><p>You can also add a compas config file at &#39;config/compas.{js,json}&#39; to specify project specific items. They are appended to the specification of the command and can be used if your tests write files that may trigger the watcher. See the <a href="https://compasjs.com/references/compas-config.html" target="_blank" rel="noopener noreferrer">Compas configuration reference</a> for more information about the allowed options.</p><div class="language-json"><pre><code><span class="token punctuation">{</span>
  <span class="token property">&quot;cli&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token property">&quot;globalWatchOptions&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
      <span class="token property">&quot;extensions&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
      <span class="token property">&quot;ignorePatterns&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token string">&quot;__fixtures__&quot;</span><span class="token punctuation">,</span> <span class="token string">&quot;test/tmp&quot;</span><span class="token punctuation">]</span>
    <span class="token punctuation">}</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><table><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td>-h, --help</td><td>Display information about the current command. (boolean)</td></tr></tbody></table>`,105),r=[s];function i(c,d,p,l,h,m){return a(),e("div",null,r)}var g=t(n,[["render",i]]);export{b as __pageData,g as default};
